const BashboardMenu = (props)=>{

    const {activeTab,setActiveTab} = props;

    return (
        <div className="p-4 flex flex-col">
            <div className={`p-4 flex flex-row  ${activeTab === 'Home' ? 'rounded bg-green-50':''}`} 
            onClick={()=>{setActiveTab('Home')}} >
               <div className="p-1">
                    <svg xmlns="http://www.w3.org/2000/svg" class="h-5 w-5" viewBox="0 0 20 20" fill="rgba(16, 185, 129, var(--tw-bg-opacity))">
                        <path d="M10.707 2.293a1 1 0 00-1.414 0l-7 7a1 1 0 001.414 1.414L4 10.414V17a1 1 0 001 1h2a1 1 0 001-1v-2a1 1 0 011-1h2a1 1 0 011 1v2a1 1 0 001 1h2a1 1 0 001-1v-6.586l.293.293a1 1 0 001.414-1.414l-7-7z" />
                    </svg>
                </div>
                <span className="p-1">Home</span>
            </div>
            <div className={`p-4 flex flex-row  ${activeTab === 'Groups' ? 'rounded bg-green-50':''}`}
                onClick={()=>{setActiveTab('Groups')}}>
                <svg xmlns="http://www.w3.org/2000/svg" class="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="rgba(16, 185, 129, var(--tw-bg-opacity))" stroke-width="2">
                    <path stroke-linecap="round" stroke-linejoin="round" d="M12 4.354a4 4 0 110 5.292M15 21H3v-1a6 6 0 0112 0v1zm0 0h6v-1a6 6 0 00-9-5.197M13 7a4 4 0 11-8 0 4 4 0 018 0z" />
                </svg>
                <span className="float-right menu-item">Groups</span>

            </div>
            <div className={`p-4 flex flex-row  ${activeTab === 'Settings' ? 'rounded bg-green-50':''}`}
                onClick={()=>{setActiveTab('Settings')}}>
                Settings
            </div>
            <div className={`p-4 ${activeTab === 'Invite' ? 'rounded bg-green-50':''}`}
                onClick={()=>{setActiveTab('Invite')}}>
                Invite
            </div>
            <div className={`p-4 ${activeTab === 'Logout' ? 'rounded bg-green-50':''}`}
                onClick={()=>{setActiveTab('Logout')}} >
                Logout
            </div>
        </div>
    );
}

export default BashboardMenu;
