import AppRoutes from "./routes/AppRoutes";
import { BrowserRouter } from "react-router-dom";
import "./styles.css";
import ErrorHandler from "./components/ErrorHandler";
import {User_Cred_Store_By_Email} from './store/'
import { AuthContext } from "./contexts/AuthContext";
import {StoreContext} from "./contexts/StoreContext"
import {useState} from 'react'
import {App_Store} from "./store/";
import addExpence from "./store/actions";
import {UiContext} from "./contexts/UiContext";
export default function App(props) {

  const {users,...rest} = {...App_Store}
  const [appStore,updateAppStore] = useState(rest)
  //App User Credential Info -- add user incase when signin.
  const [UserCredStore,updateUserCredStore] = useState({...User_Cred_Store_By_Email});
  //User info strored in the state.
  const [userInfo,updateUserInfo] = useState();
  console.log('userInfo start in app')
  console.log(userInfo)
  console.log('userInfo end in app')
  const [group,setGroup] = useState('global');

  const setUserInfo = (_userInfo)=>{
    console.log("setUserInfo got called");
    updateUserInfo(_userInfo);
  }


  const addUserToApp = (userInfo)=>{

    const {email,fullName,password, accessPoint='LNB'} = userInfo;
    const _CredStore = {UserCredStore};
    _CredStore[email] = {
      email,fullName,password,accessPoint
    }
    updateUserCredStore(_CredStore);
  }

  const addExpense = (expense)=>{
    updateAppStore(addExpence(appStore,expense))
  }

  const appAuthContext = {UserCredStore,userInfo,setUserInfo}

  return (
    <BrowserRouter forceRefresh={true} >
      <ErrorHandler>
        <UiContext.Provider value={{group,}}>
          <AuthContext.Provider value={appAuthContext}>
            <StoreContext.Provider value={{store:appStore,addExpense}}>
              <div className="App">
                <AppRoutes />
              </div>
            </StoreContext.Provider>
          </AuthContext.Provider>
        </UiContext.Provider>
      </ErrorHandler>
    </BrowserRouter>
  );
}
