import AppRoutes from "./routes/AppRoutes";
import { BrowserRouter } from "react-router-dom";
import "./styles.css";
import ErrorHandler from "./components/ErrorHandler";
import {User_Cred_Store} from './store/'
import { AuthContext } from "./components/AuthContext";
import {useState} from 'react'
export default function App(props) {

  //User info strored in the state.
  const [UserInfo,setUserInfo] = useState(null);

  //App User Credential Info -- add user incase when signin.
  const [UserCredStore,updateUserCredStore] = useState({...User_Cred_Store});

  const appAuthContext = {UserCredStore,UserInfo}

  return (
    <BrowserRouter forceRefresh={true}>
      <ErrorHandler>
        <AuthContext.Provider value={appAuthContext}>
          <div className="App">
            <AppRoutes />
          </div>
        </AuthContext.Provider>
      </ErrorHandler>
    </BrowserRouter>
  );
}
